package com.example.travelbox.presentation.view.my

import TempoAdapter
import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.GridLayoutManager
import com.example.travelbox.databinding.FragmentSouvenirsBinding

class SouvenirsFragment : Fragment() {

    private var _binding: FragmentSouvenirsBinding? = null
    private val binding get() = _binding!!

    // 예제 이미지 URL 리스트
    private val imageList = listOf(
        "data:image/jpeg;base64,",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVS4OUvqv5X7y6HSNEZiGb0CZeLCAKI2-lqA&s",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSs2XoOMaKRi5IXV2eR9varChJldwyMI5O_ig&s",
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
    )

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = FragmentSouvenirsBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        setupRecyclerView()
    }

    private fun setupRecyclerView() {
        val adapter = TempoAdapter(imageList)
        binding.recyclerSouvenirs.layoutManager = GridLayoutManager(requireContext(), 3)
        binding.recyclerSouvenirs.adapter = adapter
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
}
